import { useState } from "react";
import "./App.css";
import Radio from "./Radio";
import Modal from "./Modal";

function App() {
  const playValues = ["rock", "paper", "sissors"];
  const [radioValue, setRadioValue] = useState(null);
  const [compValue, setCompValue] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [win, setWin] = useState(false);

  function handleClick(e) {
    setRadioValue(e.target.value);
  }

  function handleSubmit() {
    const randomNumber = Math.floor(Math.random() * 3);
    setCompValue(playValues[randomNumber]);
    setIsModalOpen(true);
    switch (true) {
      case radioValue === compValue:
        setWin("Draw");
        break;
      case radioValue === "rock" && radioValue === "paper":
        setWin(false);
        break;
      case radioValue === "rock" && radioValue === "sissors":
        setWin(true);
        break;
      case radioValue === "paper" && radioValue === "rock":
        setWin(true);
        break;
      case radioValue === "paper" && radioValue === "sissors":
        setWin(false);
        break;
      case radioValue === "sissors" && radioValue === "rock":
        setWin(false);
        break;
      case radioValue === "sissors" && radioValue === "paper":
        setWin(true);
        break;
      default:
        break;
    }
    if (radioValue === playValues[randomNumber]) setWin(true);
  }

  function closeModal() {
    setIsModalOpen(false);
  }

  return (
    <div className="App">
      <h1>Rock Paper Sissors</h1>
      <Radio list={playValues} sendVal={handleClick} />
      {radioValue ? (
        <div className="data-post-selection">
          <button onClick={handleSubmit}>Play!</button>
        </div>
      ) : (
        <p>Select an Input</p>
      )}
      <Modal closeModal={closeModal} isOpen={isModalOpen}>
        {win ? <h1>You Win</h1> : <h1>Computer Win's</h1>}
        <p>
          <strong>Your Play:</strong>{" "}
          {radioValue ? radioValue.toUpperCase() : null}
        </p>
        <p>
          <strong>Computer Play:</strong>{" "}
          {compValue ? compValue.toUpperCase() : null}
        </p>
      </Modal>
    </div>
  );
}

export default App;
